import React from 'react';

const conteudos = [
  {
    conteudo: 'High Order Functions',
    bloco: 8,
    status: 'Aprendido',
  },
  {
    conteudo: 'Composicao de Componentes',
    bloco: 11,
    status: 'Aprendendo',
  },
  {
    conteudo: 'Composicao de Estados',
    bloco: 12,
    status: 'Aprenderei',
  },
  {
    conteudo: 'Redux',
    bloco: 16,
    status: 'Aprenderei',
  },
];

class Content extends React.Component {
  render() {
    return (
      <section>
        {conteudos.map(function (conteudo) {
            const block = <section key={conteudo.conteudo}>
            <p>{`O conteúdo é: ${conteudo.conteudo}`}</p>
            <p>{`Status: ${conteudo.status}`}</p>
            <p>{`Bloco: ${conteudo.bloco}`}</p><br />
          </section>;
          return block;
        })}
      </section>
    );
  }
}

export default Content;
